<script>
/**
 * Image map element
 */
export default {
  name: "VsImageLocationMap",
  status: "prototype",
  release: "0.0.1",
  data: function() {
    return {
      cornerCoordinates: {
        NE: 61.3,
        NW: 54.4,
        SE: -9.1,
        SW: 0.4,
      },
      mapWidth: 75,
      mapHeight: 104,
    }
  },
  props: {
    /**
     * The image latitude
     */
    latitude: {
      type: String,
    },

    /**
     * The image longitude
     */
    longitude: {
      type: String,
    },
  },
  computed: {
    positionY() {
      let y_coord = Number(this.latitude)

      return Math.round(
        this.calculateAxisPosition(
          this.cornerCoordinates.NE,
          this.cornerCoordinates.NW,
          y_coord,
          this.mapHeight
        )
      )
    },
    positionX() {
      let x_coord = Number(this.longitude)

      return Math.round(
        this.calculateAxisPosition(
          this.cornerCoordinates.SE,
          this.cornerCoordinates.SW,
          x_coord,
          this.mapWidth
        )
      )
    },
  },
  methods: {
    calculateAxisPosition(scaleStart, scaleEnd, coord, axisLength) {
      return ((coord - scaleStart) * axisLength) / (scaleEnd - scaleStart)
    },
  },
}
</script>

